import { H3, P, Button } from "./designSystem"
import { Box, BoxContainer } from "./Box"

const Person = ({person}) => (
  <>
    <Box>
      <div className="container">
        <div className="imageContainer">
          <img src="/images/event-graphic.png" className="image" />
        </div>
        <BoxContainer>
          <H3>{person.name}</H3>
          <P>Ariane is the co-founder of the Ottawa Design Club. The idea of being part of an inclusive community of like-minded creatives is what pushed Ariane to join forces with Isabelle and create this chapter of the Design Club in her city. Designer for over 10 years, she now works as a Senior Graphic Designer for the University of Ottawa. There, she works with a dynamic marketing team to promote the Student Experience. Multi-passionate, she loves to learn and to take on all kinds of new projects.</P>
          <Button href="">Follow on Instagram</Button>
        </BoxContainer>
      </div>
    </Box>

    <style jsx>
      {`
        .container {
          display: grid;
          grid-template-columns: 1fr 2fr;
        }

        .image {
          height: 100%;
          object-fit: cover;
        }
      `}
    </style>
  </>
)

export default Person
